import React from 'react';
import './Result.css';


const Result = (props) => {

    const{err, city, temp, date, wind, country, tempMin, tempMax, clouds, icon} = props.weather;
const icons = `http://openweathermap.org/img/wn/${icon}@2x.png`

    let content = null;

    if(!err && city){

        content = (



        <div>
            <h3>{city}, {country}</h3>
            <h4 class="date">{date}</h4>
            <h2 class="temp">{temp} &#176;C</h2>
            <h4 class="wind">Aktualna si≈Ça wiatru: {wind} m/s</h4>
            <h4 class="clouds">{clouds} </h4><img alt="Icon" src={icons}></img>
            <h4 class="tempMin">{tempMin} {tempMax} </h4>
  
        </div>
        )
    }

    return (
        <div className="result">
            {err ? `` :
            content}
        </div>


     );
}

export default Result;
