import React from 'react';
import './Result.css';

const Result = (props) => {

    const{err, city, temp, date, pressure, wind, country} = props.weather;

    let content = null;

    if(!err && city){

        content = (



        <div>
            <h3>{city}, {country}</h3>
            <h4>{date}</h4>
            <h2>Aktualna temperatura: {temp} &#176;C</h2>
            <h4>Aktualna siła wiatru: {wind} m/s</h4>
            <h4>Aktualna ciśnienie: {pressure} hPa</h4>
            
        </div>
        )
    }

    return (
        <div className="result">
            {err ? `` :
            content}
        </div>


     );
}

export default Result;
